import{_ as e,o,c as s,a as l}from"./app.382d55ed.js";const f=JSON.parse('{"title":"Install GOKZ","description":"Installation guide for GOKZ","frontmatter":{"title":"Install GOKZ","description":"Installation guide for GOKZ"},"headers":[{"level":2,"title":"1. Installing Metamod:Source","slug":"_1-installing-metamod-source"},{"level":2,"title":"2. Installing SourceMod","slug":"_2-installing-sourcemod"},{"level":2,"title":"3. Installing MovementAPI","slug":"_3-installing-movementapi"},{"level":2,"title":"4. Installing GOKZ","slug":"_4-installing-gokz"},{"level":2,"title":"5. GOKZ is installed","slug":"_5-gokz-is-installed"},{"level":2,"title":"6. Useful plugins (optional)","slug":"_6-useful-plugins-optional"},{"level":2,"title":"7. Apply for Global Whitelist (optional)","slug":"_7-apply-for-global-whitelist-optional"}],"relativePath":"guides/install-gokz.md"}'),t={name:"guides/install-gokz.md"},a=l(`<h1 id="before-you-begin" tabindex="-1">Before you begin <a class="header-anchor" href="#before-you-begin" aria-hidden="true">#</a></h1><ol><li>This guide assumes that you have a working CS:GO server.</li><li>This guide assumes that you have access to the server files.</li></ol><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>If you are asked to overwrite files at any point in the guide, accept the overwrites.</p></div><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>If you are having trouble with a step, try the <strong>#support</strong> channel in <a href="https://discord.gg/csgokz" target="_blank" rel="noreferrer">our Discord server</a>. <br>Be sure to mention which step you are having trouble with and include as much information as possible, including the error logs from <code>csgo/addons/sourcemod/logs/errors_********.log</code>.</p></div><h2 id="_1-installing-metamod-source" tabindex="-1">1. Installing Metamod:Source <a class="header-anchor" href="#_1-installing-metamod-source" aria-hidden="true">#</a></h2><ol><li><p>Start by downloading <a href="https://www.sourcemm.net/downloads.php?branch=stable" target="_blank" rel="noreferrer">Metamod:Source</a>.</p></li><li><p>Extract the files into your server&#39;s <code>csgo</code> directory.</p></li><li><p>You should now have Metamod:Source installed and working.</p></li></ol><div class="info custom-block"><p class="custom-block-title">Confirm this step</p><p>Restart your server and type <code>meta version</code> in the server console. <br>You should see the following line in the output: <code>Loaded As: Valve Server Plugin</code>.</p></div><h2 id="_2-installing-sourcemod" tabindex="-1">2. Installing SourceMod <a class="header-anchor" href="#_2-installing-sourcemod" aria-hidden="true">#</a></h2><ol><li><p>Start by downloading <a href="https://sourcemod.net/downloads.php?branch=stable" target="_blank" rel="noreferrer">SourceMod</a>.</p></li><li><p>Extract the files into your server&#39;s <code>csgo</code> directory.</p></li><li><p>You should now have SourceMod installed and working.</p></li></ol><div class="info custom-block"><p class="custom-block-title">Confirm this step</p><p>Restart your server and type <code>sm version</code> in the server console. <br>You should see the following line in the output: <code>SourceMod Version Information</code>.</p></div><h2 id="_3-installing-movementapi" tabindex="-1">3. Installing MovementAPI <a class="header-anchor" href="#_3-installing-movementapi" aria-hidden="true">#</a></h2><ol><li><p>Start by downloading the latest release of <a href="https://github.com/danzayau/MovementAPI/releases" target="_blank" rel="noreferrer">MovementAPI</a>.</p></li><li><p>Extract the files into your server&#39;s <code>csgo</code> directory.</p></li><li><p>You should now have MovementAPI installed and working.</p></li></ol><div class="info custom-block"><p class="custom-block-title">Confirm this step</p><p>Restart your server and type <code>sm plugins list</code> in the server console. <br>You should see the following line in the output: <code>MovementAPI (2.2.0)</code>.</p></div><h2 id="_4-installing-gokz" tabindex="-1">4. Installing GOKZ <a class="header-anchor" href="#_4-installing-gokz" aria-hidden="true">#</a></h2><ol><li><p>Start by downloading the latest <strong>non-upgrade</strong> release of <a href="https://github.com/KZGlobalTeam/gokz/releases" target="_blank" rel="noreferrer">GOKZ</a>.</p></li><li><p>Extract the files into your server&#39;s <code>csgo</code> directory.</p></li><li><p>Open <code>csgo/addons/sourcemod/configs/databases.cfg</code> in a text editor and add the following lines:</p></li></ol><div class="language-json"><button class="copy"></button><span class="lang">json</span><pre><code><span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C3E88D;">gokz</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#89DDFF;">{</span></span>
<span class="line"><span style="color:#A6ACCD;">        </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C792EA;">driver</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;">    </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C792EA;">sqlite</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#A6ACCD;">        </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C792EA;">database</span><span style="color:#89DDFF;">&quot;</span><span style="color:#A6ACCD;">  </span><span style="color:#89DDFF;">&quot;</span><span style="color:#C792EA;">gokz-sqlite</span><span style="color:#89DDFF;">&quot;</span></span>
<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#89DDFF;">}</span></span>
<span class="line"></span></code></pre></div><div class="danger custom-block"><p class="custom-block-title">DANGER</p><p>Make sure to paste this BEFORE the last existing <code>}</code> character in the file.</p></div><ol start="4"><li>You should now have GOKZ installed and working.</li></ol><div class="info custom-block"><p class="custom-block-title">Confirm this step</p><p>Restart your server and type <code>sm plugins list</code> in the server console. <br>You should see the following line in the output: <code>GOKZ-Core (3.2.0)</code>.</p></div><h2 id="_5-gokz-is-installed" tabindex="-1">5. GOKZ is installed <a class="header-anchor" href="#_5-gokz-is-installed" aria-hidden="true">#</a></h2><p>You&#39;ve finished installing GOKZ, the next steps are not required for a functional GOKZ server.</p><h2 id="_6-useful-plugins-optional" tabindex="-1">6. Useful plugins (optional) <a class="header-anchor" href="#_6-useful-plugins-optional" aria-hidden="true">#</a></h2><p>Install other useful plugins from the <a href="/guides/useful-plugins">Useful plugins</a> guide.</p><h2 id="_7-apply-for-global-whitelist-optional" tabindex="-1">7. Apply for Global Whitelist (optional) <a class="header-anchor" href="#_7-apply-for-global-whitelist-optional" aria-hidden="true">#</a></h2><p>Follow the <a href="/guides/global-gokz">GOKZ global whitelist guide</a> guide to get your server whitelisted.</p>`,25),n=[a];function i(r,c,p,d,u,h){return o(),s("div",null,n)}const m=e(t,[["render",i]]);export{f as __pageData,m as default};
